          val users = mutableListOf<User>()
          val user1 = User(
               1,
               "Juan Pablo",
               "Chechi",
               "data:image/jpeg;base64,"
          )
          val user2 = User(
               2,
               "Agustin",
               "Chechi",
               "data:image/jpeg;base64,"
          )
          val user3 = User(
               3,
               "Francisco",
               "Chechi",
               "data:image/jpeg;base64,"
          )
          val user4 = User(
               4,
               "Martina",
               "Entre Rios",
               "data:image/jpeg;base64,"
          )
          val user5 = User(
               5,
               "Pedro",
               "Perez",
               "data:image/jpeg;base64,"
          )
          val user6 = User(
               6,
               "Guillermo",
               "Funes",
               "data:image/jpeg;base64,"
          )
          val user7 = User(
               7,
               "Tomas",
               "Gil",
               "data:image/jpeg;base64,"
          )
          val user8 = User(
               1,
               "Juan Pablo",
               "Chechi",
               "data:image/jpeg;base64,"
          )
          users.add(user1)
          users.add(user2)
          users.add(user3)
          users.add(user4)
          users.add(user5)
          users.add(user6)
          users.add(user7)
          users.add(user8)
          return users
     }


     /*
         -----------------------------
         METODO QUE AL HACER CLICK EN CADA OBJETO MUESTRA UNA VENTANA FLOTANTE CON EL NOMBRE
         /// EL Toast.makeText es el encargado de generarl esa ventana flotante
         ----------------------------
          */
     override fun onClick(user: User) {
          Toast.makeText(
               this, user.id.toString() + ": " + user.getFullName() + " <--", Toast.LENGTH_SHORT
          ).show()
     }


}